const content = $json.message.content
const contentString =
    typeof content === 'string' ? content : JSON.stringify(content);

  // Extract content from XML tags using regex
const thinkMatch = contentString.match(/<THINK>([\s\S]*?)<\/THINK>/);
  const classificationMatch = contentString.match(
    /<CLASSIFICATION>([\s\S]*?)<\/CLASSIFICATION>/,
  );

if (!thinkMatch || !classificationMatch) {
    throw new Error('Response missing required XML tags');
  }

const thinkContent = thinkMatch[1].trim();
const classificationContent = classificationMatch[1].trim();

// Parse the classification JSON
const classification = JSON.parse(
    classificationContent,
  )

  return {
    json: {
    think: thinkContent,
    classification,
  }
  };

const clientLeads = $('Aggregate Client Leads').first().json.clientLeads;

const currentLead = $('Fetch Profile').item.json;

const foundClientLead = clientLeads.find(lead => {
  const linkedinUrl = lead["LinkedIn URL"];
  return linkedinUrl && linkedinUrl.includes(currentLead.provider_id);
});

if (foundClientLead) {
  return {
    json: {
      ...foundClientLead,
      exist: true
    }
  };
} else {
  return {
    json: {
      exist: false
    }
  };
}

